import {
  pgTable,
  varchar,
  pgEnum,
  uuid,
  timestamp,
  text,
} from "drizzle-orm/pg-core";
import { sql } from "drizzle-orm";

const status = pgEnum("status", [
  "NEW",
  "COMPLETED",
  "TERMINATED",
  "TERMINATED",
])()
  .default("NEW")
  .notNull();

const timestamps = {
  createdAt: timestamp().notNull().defaultNow(),
  updatedAt: timestamp(),
  archivedAt: timestamp(),
};

export const users = pgTable("projects", {
  // autogenerated UID
  uid: uuid(),
  // required project name
  name: varchar({ length: 64 }).notNull().unique(),
  // tag list, defaults to empty list
  // https://orm.drizzle.team/docs/guides/empty-array-default-value#postgresql
  tags: text("tags")
    .array()
    .notNull()
    .default(sql`'{}'::text[]`),
  // required project description
  description: varchar({ length: 256 }).notNull(),
  status,
  ...timestamps,
});
